function reverseWords(str) {
    // split the string into an array of words
    var words = str.split(' ');

    // reverse each word in the array
    for (var i = 0; i < words.length; i++) {
        words[i] = words[i].split('').reverse().join('');
    }

    // join the reversed words back into a string
    return words.join(' ');
}

